import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStudents();
    fetchCourses();
  }, []);

  const fetchStudents = async () => {
    try {
      const res = await fetch("http://localhost:5000/students");
      const data = await res.json();
      setStudents(data);
    } catch (error) {
      console.error("Error fetching students:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchCourses = async () => {
    try {
      const res = await fetch("http://localhost:5000/courses");
      const data = await res.json();
      setCourses(data);
    } catch (error) {
      console.error("Error fetching courses:", error);
    }
  };

  const handleStudentSubmit = async (e) => {
    e.preventDefault();
    const url = editingStudent
      ? `http://localhost:5000/students/update/${editingStudent._id}`
      : "http://localhost:5000/students/add";
    const method = editingStudent ? "PUT" : "POST";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email }),
      });
      const data = await res.json();
      alert(data.message);

      if (editingStudent) {
        setStudents((prev) =>
          prev.map((s) => (s._id === editingStudent._id ? data.student : s))
        );
        setEditingStudent(null);
      } else {
        setStudents([...students, data.student]);
      }
      setName("");
      setEmail("");
    } catch (error) {
      console.error("Error saving student:", error);
    }
  };

  const deleteStudent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this student?"))
      return;
    try {
      const res = await fetch(`http://localhost:5000/students/delete/${id}`, {
        method: "DELETE",
      });
      const data = await res.json();
      alert(data.message);
      setStudents(students.filter((s) => s._id !== id));
    } catch (error) {
      console.error("Error deleting student:", error);
    }
  };

  const enrollStudent = async (studentId) => {
    const courseId = selectedCourses[studentId];
    if (!courseId) {
      alert("Please select a course first.");
      return;
    }

    try {
      const res = await fetch(
        `http://localhost:5000/students/enroll/${studentId}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ courseId }),
        }
      );

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to enroll student");

      alert(`Success: ${data.message}`);
      fetchStudents();
      setSelectedCourses((prev) => {
        const newState = { ...prev };
        delete newState[studentId];
        return newState;
      });
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6 bg-gray-100 min-h-screen">
      <h2 className="text-3xl font-bold text-gray-700 text-center mb-6">
        🎓 Student Management
      </h2>

      {/* Student Form */}
      <div className="bg-white p-6 shadow-md rounded-lg mb-6">
        <form onSubmit={handleStudentSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-600 font-medium">Name</label>
            <input
              type="text"
              className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-400"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div>
            <label className="block text-gray-600 font-medium">Email</label>
            <input
              type="email"
              className="w-full p-2 border rounded-md focus:ring-2 focus:ring-blue-400"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="flex space-x-3">
            <button
              type="submit"
              className={`px-4 py-2 text-white font-medium rounded ${
                editingStudent
                  ? "bg-blue-500 hover:bg-blue-600"
                  : "bg-green-500 hover:bg-green-600"
              }`}
            >
              {editingStudent ? "Update Student" : "Add Student"}
            </button>
            {editingStudent && (
              <button
                type="button"
                className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
                onClick={() => {
                  setEditingStudent(null);
                  setName("");
                  setEmail("");
                }}
              >
                Cancel
              </button>
            )}
          </div>
        </form>
      </div>

      {/* Students List */}
      {loading ? (
        <p className="text-center text-gray-500">Loading students...</p>
      ) : students.length === 0 ? (
        <p className="text-center text-gray-500">No students found.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {students.map((student) => (
            <div
              key={student._id}
              className="bg-white p-6 shadow-md rounded-lg"
            >
              <h3 className="text-lg font-bold text-gray-800">
                {student.name}
              </h3>
              <p className="text-gray-500">{student.email}</p>

              <div className="mt-4">
                <label className="block text-gray-600 font-medium">
                  Enroll in Course
                </label>
                <div className="flex space-x-2 mt-2">
                  <select
                    className="flex-1 p-2 border rounded-md"
                    onChange={(e) =>
                      setSelectedCourses({
                        ...selectedCourses,
                        [student._id]: e.target.value,
                      })
                    }
                    value={selectedCourses[student._id] || ""}
                  >
                    <option value="">Select Course</option>
                    {courses.map((course) => (
                      <option key={course._id} value={course._id}>
                        {course.name}
                      </option>
                    ))}
                  </select>
                  <button
                    onClick={() => enrollStudent(student._id)}
                    className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                  >
                    Enroll
                  </button>
                </div>
              </div>

              <div className="mt-4 flex space-x-3">
                <button
                  onClick={() => deleteStudent(student._id)}
                  className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Students;
