import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import styled from "styled-components";
import {
  FiEdit2,
  FiTrash2,
  FiPlus,
  FiX,
  FiBook,
  FiUser,
  FiMail,
} from "react-icons/fi";

// Styled Components
const Card = styled(motion.div)`
  background: white;
  border-radius: 16px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  overflow: hidden;
  transition: all 0.3s cubic-bezier(0.25, 0.8, 0.25, 1);
  display: flex;
  flex-direction: column;
  position: relative;

  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 4px;
    background: linear-gradient(90deg, #3498db, #2ecc71);
  }
`;

const EmptyState = styled(motion.div)`
  background: white;
  padding: 3rem;
  border-radius: 16px;
  text-align: center;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  grid-column: 1 / -1;
`;

const Students = () => {
  // ... existing state and methods ...

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <motion.div
        style={styles.header}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <motion.h2
          style={styles.heading}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.2 }}
        >
          <span style={styles.icon}>👨‍🎓</span> Student Management
        </motion.h2>
        <motion.p
          style={styles.subHeading}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3 }}
        >
          Comprehensive student administration and enrollment system
        </motion.p>
      </motion.div>

      {/* Student Form */}
      <motion.div
        style={styles.formContainer}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.4 }}
      >
        <form
          onSubmit={editingStudent ? updateStudent : addStudent}
          style={styles.form}
        >
          <div style={styles.formHeader}>
            <h3 style={styles.formTitle}>
              {editingStudent ? "Edit Student" : "Add New Student"}
            </h3>
            {editingStudent && (
              <button
                type="button"
                onClick={() => {
                  setEditingStudent(null);
                  setName("");
                  setEmail("");
                }}
                style={styles.closeButton}
              >
                <FiX size={20} />
              </button>
            )}
          </div>

          <div style={styles.formGrid}>
            <div style={styles.inputGroup}>
              <label style={styles.label}>
                <FiUser style={styles.inputIcon} /> Student Name
              </label>
              <input
                type="text"
                placeholder="John Doe"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                style={styles.input}
              />
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.label}>
                <FiMail style={styles.inputIcon} /> Email Address
              </label>
              <input
                type="email"
                placeholder="john@university.edu"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                style={styles.input}
              />
            </div>
          </div>

          <motion.button
            type="submit"
            style={editingStudent ? styles.updateButton : styles.addButton}
            whileHover={{ y: -2 }}
            whileTap={{ scale: 0.98 }}
          >
            {editingStudent ? (
              <>
                <FiEdit2 style={styles.buttonIcon} /> Update Student
              </>
            ) : (
              <>
                <FiPlus style={styles.buttonIcon} /> Add Student
              </>
            )}
          </motion.button>
        </form>
      </motion.div>

      {/* Student Cards Grid */}
      {students.length === 0 ? (
        <EmptyState
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          <div style={styles.emptyIcon}>📚</div>
          <h3 style={styles.emptyTitle}>No Students Found</h3>
          <p style={styles.emptySubtitle}>Add your first student to begin</p>
        </EmptyState>
      ) : (
        <motion.div
          style={styles.cardGrid}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          <AnimatePresence>
            {students.map((student) => (
              <Card
                key={student._id}
                layout
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.9 }}
                transition={{ type: "spring", stiffness: 300 }}
                whileHover={{ y: -5 }}
              >
                <div style={styles.cardHeader}>
                  <h3 style={styles.studentName}>{student.name}</h3>
                  <p style={styles.studentEmail}>{student.email}</p>
                </div>

                <div style={styles.cardBody}>
                  <div style={styles.section}>
                    <h4 style={styles.sectionTitle}>
                      <FiBook style={styles.sectionIcon} /> Enrolled Courses
                    </h4>
                    <ul style={styles.courseList}>
                      {student.courses.length > 0 ? (
                        student.courses.map((course) => (
                          <motion.li
                            key={course._id}
                            style={styles.courseItem}
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                          >
                            <span style={styles.courseName}>{course.name}</span>
                          </motion.li>
                        ))
                      ) : (
                        <li style={styles.noCourseItem}>No enrolled courses</li>
                      )}
                    </ul>
                  </div>

                  <div style={styles.section}>
                    <h4 style={styles.sectionTitle}>Enroll in New Course</h4>
                    <div style={styles.enrollSection}>
                      <select
                        onChange={(e) =>
                          handleCourseSelect(student._id, e.target.value)
                        }
                        value={selectedCourses[student._id] || ""}
                        style={styles.dropdown}
                      >
                        <option value="">Select Course...</option>
                        {courses.map((course) => (
                          <option key={course._id} value={course._id}>
                            {course.name}
                          </option>
                        ))}
                      </select>
                      <motion.button
                        onClick={() => enrollStudent(student._id)}
                        style={styles.enrollButton}
                        disabled={!selectedCourses[student._id]}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        Enroll
                      </motion.button>
                    </div>
                  </div>
                </div>

                <div style={styles.cardFooter}>
                  <motion.button
                    onClick={() => {
                      setEditingStudent(student);
                      setName(student.name);
                      setEmail(student.email);
                    }}
                    style={styles.editButton}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <FiEdit2 style={styles.actionIcon} /> Edit
                  </motion.button>
                  <motion.button
                    onClick={() => deleteStudent(student._id)}
                    style={styles.deleteButton}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <FiTrash2 style={styles.actionIcon} /> Delete
                  </motion.button>
                </div>
              </Card>
            ))}
          </AnimatePresence>
        </motion.div>
      )}
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "1400px",
    margin: "0 auto",
    padding: "2rem",
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif",
    color: "#2d3436",
    minHeight: "100vh",
  },
  header: {
    textAlign: "center",
    marginBottom: "3rem",
  },
  icon: {
    marginRight: "12px",
    display: "inline-block",
  },
  heading: {
    fontSize: "2.75rem",
    fontWeight: "800",
    color: "#2c3e50",
    marginBottom: "0.5rem",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  subHeading: {
    fontSize: "1.25rem",
    color: "#7f8c8d",
    maxWidth: "700px",
    margin: "0 auto",
    lineHeight: "1.6",
  },
  formContainer: {
    background: "white",
    borderRadius: "16px",
    padding: "2rem",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.05)",
    marginBottom: "3rem",
  },
  formHeader: {
