    color: "#e74c3c",
    cursor: "pointer",
    padding: "0.5rem",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    transition: "all 0.2s ease",

    "&:hover": {
      background: "rgba(231, 76, 60, 0.1)",
