import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    fetch("http://localhost:5000/students")
      .then((response) => response.json())
      .then((data) => setStudents(data))
      .catch((error) => console.error("Error fetching students:", error));
  };

  const addStudent = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/students/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setStudents([...students, data.student]);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error adding student:", error));
  };

  const updateStudent = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/students/update/${editingStudent._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setStudents((prev) =>
          prev.map((s) => (s._id === editingStudent._id ? data.student : s))
        );
        setEditingStudent(null);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error updating student:", error));
  };

  const deleteStudent = (id) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      fetch(`http://localhost:5000/students/delete/${id}`, { method: "DELETE" })
        .then((response) => response.json())
        .then((data) => {
          alert(data.message);
          setStudents(students.filter((s) => s._id !== id));
        })
        .catch((error) => console.error("Error deleting student:", error));
    }
  };

  return (
    <div className="container">
      <h2 className="heading">🎓 Student Management</h2>

      <form
        onSubmit={editingStudent ? updateStudent : addStudent}
        className="form"
      >
        <input
          type="text"
          placeholder="Enter student name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          className="input"
        />
        <input
          type="email"
          placeholder="Enter student email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          className="input"
        />
        <button type="submit" className="add-button">
          {editingStudent ? "✏️ Update Student" : "➕ Add Student"}
        </button>
        {editingStudent && (
          <button
            onClick={() => setEditingStudent(null)}
            className="cancel-button"
          >
            ❌ Cancel
          </button>
        )}
      </form>

      {students.length === 0 ? (
        <p className="no-students">No students found.</p>
      ) : (
        <div className="card-grid">
          {students.map((student) => (
            <div key={student._id} className="card">
              <h3 className="student-name">{student.name}</h3>
              <p className="student-email">{student.email}</p>
              <div className="button-group">
                <button
                  onClick={() => {
                    setEditingStudent(student);
                    setName(student.name);
                    setEmail(student.email);
                  }}
                  className="edit-button"
                >
                  ✏️ Edit
                </button>
                <button
                  onClick={() => deleteStudent(student._id)}
                  className="delete-button"
                >
                  🗑️ Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Students;
