import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    fetch("http://localhost:5000/students")
      .then((response) => response.json())
      .then((data) => setStudents(data))
      .catch((error) => console.error("Error fetching students:", error));
  };

  const addStudent = (e) => {
    e.preventDefault();

    fetch("http://localhost:5000/students/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        fetchStudents();
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error adding student:", error));
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>ðŸŽ“ Students Page</h2>
      <p style={styles.description}>
        Manage students and track their progress.
      </p>

      <form onSubmit={addStudent} style={styles.form}>
        <input
          type="text"
          placeholder="Enter student name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="email"
          placeholder="Enter student email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.button}>
          âž• Add Student
        </button>
      </form>

      {students.length === 0 ? (
        <p style={styles.noStudents}>No students found.</p>
      ) : (
        <ul style={styles.list}>
          {students.map((student) => (
            <li key={student._id} style={styles.listItem}>
              {student.name} - {student.email}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "600px",
    margin: "auto",
    padding: "20px",
    background: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
  },
  title: {
    fontSize: "24px",
    color: "#333",
  },
  description: {
    fontSize: "16px",
    color: "#666",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginTop: "20px",
  },
  input: {
    padding: "10px",
    fontSize: "16px",
    border: "1px solid #ccc",
    borderRadius: "4px",
    outline: "none",
  },
  button: {
    padding: "10px",
    fontSize: "16px",
    background: "#007bff",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    transition: "background 0.3s",
  },
  buttonHover: {
    background: "#0056b3",
  },
  noStudents: {
    color: "#888",
    marginTop: "20px",
  },
  list: {
    marginTop: "20px",
    padding: "0",
    listStyle: "none",
  },
  listItem: {
    background: "white",
    padding: "10px",
    margin: "5px 0",
    borderRadius: "4px",
    boxShadow: "0 0 5px rgba(0, 0, 0, 0.1)",
  },
};

export default Students;
