import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);

  useEffect(() => {
    fetchStudents();
  }, []);

  // ‚úÖ Fetch students
  const fetchStudents = () => {
    fetch("http://localhost:5000/students")
      .then((response) => response.json())
      .then((data) => setStudents(data))
      .catch((error) => console.error("Error fetching students:", error));
  };

  // ‚úÖ Add student
  const addStudent = (e) => {
    e.preventDefault();

    fetch("http://localhost:5000/students/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setStudents([...students, data.student]); // Instant update
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error adding student:", error));
  };

  // ‚úÖ Update student
  const updateStudent = (e) => {
    e.preventDefault();

    fetch(`http://localhost:5000/students/update/${editingStudent._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);

        // ‚úÖ Instant state update
        setStudents((prev) =>
          prev.map((s) => (s._id === editingStudent._id ? data.student : s))
        );

        setEditingStudent(null);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error updating student:", error));
  };

  // ‚úÖ Delete student
  const deleteStudent = (id) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      fetch(`http://localhost:5000/students/delete/${id}`, { method: "DELETE" })
        .then((response) => response.json())
        .then((data) => {
          alert(data.message);
          setStudents(students.filter((s) => s._id !== id)); // Instant removal
        })
        .catch((error) => console.error("Error deleting student:", error));
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>üéì Student Management</h2>

      {/* ‚úÖ Student Form */}
      <form
        onSubmit={editingStudent ? updateStudent : addStudent}
        style={styles.form}
      >
        <input
          type="text"
          placeholder="Enter student name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="email"
          placeholder="Enter student email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.button}>
          {editingStudent ? "‚úèÔ∏è Update Student" : "‚ûï Add Student"}
        </button>
        {editingStudent && (
          <button
            onClick={() => setEditingStudent(null)}
            style={styles.cancelButton}
          >
            ‚ùå Cancel
          </button>
        )}
      </form>

      {/* ‚úÖ Student List */}
      {students.length === 0 ? (
        <p>No students found.</p>
      ) : (
        <ul style={styles.list}>
          {students.map((student) => (
            <li key={student._id} style={styles.listItem}>
              {student.name} - {student.email}
              <button
                onClick={() => {
                  setEditingStudent(student);
                  setName(student.name);
                  setEmail(student.email);
                }}
                style={styles.editButton}
              >
                ‚úèÔ∏è Edit
              </button>
              <button
                onClick={() => deleteStudent(student._id)}
                style={styles.deleteButton}
              >
                üóëÔ∏è Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

// ‚úÖ Inline CSS Styling
const styles = {
  container: { maxWidth: "600px", margin: "auto", textAlign: "center" },
  heading: { fontSize: "24px", fontWeight: "bold" },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginBottom: "20px",
  },
  input: { padding: "10px", fontSize: "16px" },
  button: {
    backgroundColor: "#28a745",
    color: "white",
    padding: "10px",
    cursor: "pointer",
    border: "none",
  },
  cancelButton: {
    backgroundColor: "#dc3545",
    color: "white",
    padding: "10px",
    cursor: "pointer",
    border: "none",
  },
  list: { listStyle: "none", padding: 0 },
  listItem: {
    display: "flex",
    justifyContent: "space-between",
    padding: "10px",
    borderBottom: "1px solid #ddd",
  },
  editButton: {
    backgroundColor: "#ffc107",
    border: "none",
    padding: "5px",
    cursor: "pointer",
  },
  deleteButton: {
    backgroundColor: "#dc3545",
    border: "none",
    padding: "5px",
    cursor: "pointer",
  },
};

export default Students;
