import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState("");

  useEffect(() => {
    fetchStudents();
    fetchCourses();
  }, []);

  const fetchStudents = () => {
    fetch("http://localhost:5000/students")
      .then((response) => response.json())
      .then((data) => setStudents(data))
      .catch((error) => console.error("Error fetching students:", error));
  };

  const fetchCourses = () => {
    fetch("http://localhost:5000/courses")
      .then((response) => response.json())
      .then((data) => setCourses(data))
      .catch((error) => console.error("Error fetching courses:", error));
  };

  const addStudent = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/students/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setStudents([...students, data.student]);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error adding student:", error));
  };

  const updateStudent = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/students/update/${editingStudent._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setStudents((prev) =>
          prev.map((s) => (s._id === editingStudent._id ? data.student : s))
        );
        setEditingStudent(null);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error updating student:", error));
  };

  const deleteStudent = (id) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      fetch(`http://localhost:5000/students/delete/${id}`, { method: "DELETE" })
        .then((response) => response.json())
        .then((data) => {
          alert(data.message);
          setStudents(students.filter((s) => s._id !== id));
        })
        .catch((error) => console.error("Error deleting student:", error));
    }
  };

  const enrollStudent = (studentId) => {
    if (!selectedCourse) return alert("Please select a course");
    fetch(`http://localhost:5000/students/enroll/${studentId}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ courseId: selectedCourse }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        fetchStudents();
      })
      .catch((error) => console.error("Error enrolling student:", error));
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>🎓 Student Management</h2>

      {/* Student Form */}
      <form
        onSubmit={editingStudent ? updateStudent : addStudent}
        style={styles.form}
      >
        <input
          type="text"
          placeholder="Enter student name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="email"
          placeholder="Enter student email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.addButton}>
          {editingStudent ? "✏️ Update Student" : "➕ Add Student"}
        </button>
        {editingStudent && (
          <button
            onClick={() => setEditingStudent(null)}
            style={styles.cancelButton}
          >
            ❌ Cancel
          </button>
        )}
      </form>

      {/* Student List */}
      {students.length === 0 ? (
        <p style={styles.noStudents}>No students found.</p>
      ) : (
        <div style={styles.cardGrid}>
          {students.map((student) => (
            <div key={student._id} style={styles.card}>
              <h3 style={styles.studentName}>{student.name}</h3>
              <p style={styles.studentEmail}>{student.email}</p>
              <strong>Enrolled Courses:</strong>
              <ul style={styles.courseList}>
                {student.courses.length > 0 ? (
                  student.courses.map((course) => (
                    <li key={course._id}>{course.name}</li>
                  ))
                ) : (
                  <li>No enrolled courses</li>
                )}
              </ul>

              {/* Enroll Section */}
              <div style={styles.enrollSection}>
                <select
                  onChange={(e) => setSelectedCourse(e.target.value)}
                  style={styles.dropdown}
                >
                  <option value="">Select Course</option>
                  {courses.map((course) => (
                    <option key={course._id} value={course._id}>
                      {course.name}
                    </option>
                  ))}
                </select>
                <button
                  onClick={() => enrollStudent(student._id)}
                  style={styles.enrollButton}
                >
                  📚 Enroll
                </button>
              </div>

              {/* Edit & Delete Buttons */}
              <div style={styles.buttonGroup}>
                <button
                  onClick={() => {
                    setEditingStudent(student);
                    setName(student.name);
                    setEmail(student.email);
                  }}
                  style={styles.editButton}
                >
                  ✏️ Edit
                </button>
                <button
                  onClick={() => deleteStudent(student._id)}
                  style={styles.deleteButton}
                >
                  🗑️ Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// ✅ Updated CSS for better alignment & structure
const styles = {
  container: {
    maxWidth: "800px",
    margin: "auto",
    padding: "20px",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
  },
  heading: {
    fontSize: "28px",
    fontWeight: "bold",
    marginBottom: "20px",
    color: "#333",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  addButton: {
    backgroundColor: "#28a745",
    color: "white",
    padding: "10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  cardGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
    gap: "15px",
  },
  card: {
    backgroundColor: "#fff",
    padding: "15px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    textAlign: "left",
  },
  enrollSection: {
    display: "flex",
    gap: "10px",
    marginTop: "10px",
  },
  dropdown: {
    padding: "8px",
  },
  enrollButton: {
    backgroundColor: "#007bff",
    color: "white",
    padding: "8px",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Students;
