import React, { useEffect, useState } from "react";

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [editingCourse, setEditingCourse] = useState(null);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = () => {
    fetch("http://localhost:5000/courses")
      .then((response) => response.json())
      .then((data) => setCourses(data))
      .catch((error) => console.error("Error fetching courses:", error));
  };

  const addCourse = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/courses/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, description }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success && data.course) {
          alert(data.message);
          setCourses([...courses, data.course]);
          setName("");
          setDescription("");
        } else {
          alert("Error: Course could not be added!");
          console.error("Unexpected response:", data);
        }
      })
      .catch((error) => console.error("Error adding course:", error));
  };

  const updateCourse = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/courses/update/${editingCourse._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, description }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success && data.course) {
          alert(data.message);
          setCourses((prev) =>
            prev.map((c) => (c._id === editingCourse._id ? data.course : c))
          );
          setEditingCourse(null);
          setName("");
          setDescription("");
        } else {
          alert("Error: Course could not be updated!");
        }
      })
      .catch((error) => console.error("Error updating course:", error));
  };

  const deleteCourse = (id) => {
    if (window.confirm("Are you sure you want to delete this course?")) {
      fetch(`http://localhost:5000/courses/delete/${id}`, { method: "DELETE" })
        .then((response) => response.json())
        .then((data) => {
          alert(data.message);
          setCourses(courses.filter((c) => c._id !== id));
        })
        .catch((error) => console.error("Error deleting course:", error));
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>üìö Course Management</h2>

      {/* Course Form */}
      <form
        onSubmit={editingCourse ? updateCourse : addCourse}
        style={styles.form}
      >
        <input
          type="text"
          placeholder="Enter course name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="text"
          placeholder="Enter course description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.addButton}>
          {editingCourse ? "‚úèÔ∏è Update Course" : "‚ûï Add Course"}
        </button>
        {editingCourse && (
          <button
            onClick={() => setEditingCourse(null)}
            style={styles.cancelButton}
          >
            ‚ùå Cancel
          </button>
        )}
      </form>

      {/* Course List */}
      {courses.length === 0 ? (
        <p style={styles.noCourses}>No courses found.</p>
      ) : (
        <div style={styles.cardGrid}>
          {courses.map((course) => (
            <div key={course._id} style={styles.card}>
              <h3 style={styles.courseName}>{course.name}</h3>
              <p style={styles.courseDescription}>{course.description}</p>
              <div style={styles.buttonGroup}>
                <button
                  onClick={() => {
                    setEditingCourse(course);
                    setName(course.name);
                    setDescription(course.description);
                  }}
                  style={styles.editButton}
                >
                  ‚úèÔ∏è Edit
                </button>
                <button
                  onClick={() => deleteCourse(course._id)}
                  style={styles.deleteButton}
                >
                  üóëÔ∏è Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const styles = {
  container: { maxWidth: "800px", margin: "auto", padding: "20px" },
  heading: { fontSize: "28px", fontWeight: "bold", marginBottom: "20px" },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginBottom: "20px",
  },
  input: { padding: "10px", fontSize: "16px", borderRadius: "5px" },
  addButton: {
    backgroundColor: "#28a745",
    color: "white",
    padding: "10px",
    borderRadius: "5px",
  },
  cancelButton: {
    backgroundColor: "#dc3545",
    color: "white",
    padding: "10px",
    borderRadius: "5px",
  },
  noCourses: { fontSize: "18px", fontWeight: "bold" },
  cardGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
    gap: "15px",
  },
  card: {
    backgroundColor: "#fff",
    padding: "15px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  courseName: { fontSize: "18px", fontWeight: "bold" },
  courseDescription: { fontSize: "14px", color: "#555" },
  buttonGroup: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "10px",
  },
  editButton: {
    backgroundColor: "#ffc107",
    borderRadius: "5px",
    padding: "8px 12px",
  },
  deleteButton: {
    backgroundColor: "#dc3545",
    borderRadius: "5px",
    padding: "8px 12px",
  },
};

export default Courses;
