import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    fetch("http://localhost:5000/students")
      .then((response) => response.json())
      .then((data) => setStudents(data))
      .catch((error) => console.error("Error fetching students:", error));
  };

  const addStudent = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/students/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        fetchStudents();
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error adding student:", error));
  };

  const updateStudent = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/students/update/${editingStudent._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        fetchStudents();
        setEditingStudent(null);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error updating student:", error));
  };

  const deleteStudent = (id) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      fetch(`http://localhost:5000/students/delete/${id}`, {
        method: "DELETE",
      })
        .then((response) => response.json())
        .then((data) => {
          alert(data.message);
          fetchStudents();
        })
        .catch((error) => console.error("Error deleting student:", error));
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üéì Students Page</h2>
      <p>Manage students and track their progress.</p>

      <form
        style={styles.form}
        onSubmit={editingStudent ? updateStudent : addStudent}
      >
        <input
          type="text"
          placeholder="Enter student name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="email"
          placeholder="Enter student email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.button}>
          {editingStudent ? "‚úèÔ∏è Update Student" : "‚ûï Add Student"}
        </button>
      </form>

      {students.length === 0 ? (
        <p>No students found.</p>
      ) : (
        <ul style={styles.list}>
          {students.map((student) => (
            <li key={student._id} style={styles.listItem}>
              <span>
                {student.name} - {student.email}
              </span>
              <div>
                <button
                  style={styles.editButton}
                  onClick={() => {
                    setEditingStudent(student);
                    setName(student.name);
                    setEmail(student.email);
                  }}
                >
                  ‚úèÔ∏è Edit
                </button>
                <button
                  style={styles.deleteButton}
                  onClick={() => deleteStudent(student._id)}
                >
                  üóëÔ∏è Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
    backgroundColor: "#f4f4f4",
    minHeight: "100vh",
  },
  title: {
    fontSize: "24px",
    fontWeight: "bold",
    color: "#333",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    maxWidth: "400px",
    margin: "auto",
    padding: "20px",
    backgroundColor: "#fff",
    boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
    borderRadius: "8px",
  },
  input: {
    padding: "12px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  button: {
    padding: "12px",
    fontSize: "16px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  list: {
    listStyleType: "none",
    padding: 0,
    maxWidth: "500px",
    margin: "20px auto",
    backgroundColor: "#fff",
    boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
    borderRadius: "8px",
    padding: "10px",
  },
  listItem: {
    display: "flex",
    justifyContent: "space-between",
    padding: "12px",
    borderBottom: "1px solid #ddd",
  },
  editButton: {
    padding: "8px 12px",
    backgroundColor: "#FFC107",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    marginRight: "5px",
  },
  deleteButton: {
    padding: "8px 12px",
    backgroundColor: "#DC3545",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    color: "#fff",
  },
};

export default Students;
