import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchStudents();
    fetchCourses();
  }, []);

  const fetchStudents = async () => {
    try {
      setIsLoading(true);
      const response = await fetch("http://localhost:5000/students");
      const data = await response.json();
      setStudents(data);
    } catch (error) {
      console.error("Error fetching students:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchCourses = async () => {
    try {
      const response = await fetch("http://localhost:5000/courses");
      const data = await response.json();
      setCourses(data);
    } catch (error) {
      console.error("Error fetching courses:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = editingStudent
      ? `http://localhost:5000/students/update/${editingStudent._id}`
      : "http://localhost:5000/students/add";

    const method = editingStudent ? "PUT" : "POST";

    try {
      const response = await fetch(endpoint, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email }),
      });
      const data = await response.json();

      if (!response.ok) throw new Error(data.message || "Operation failed");

      alert(data.message);
      if (editingStudent) {
        setStudents(
          students.map((s) => (s._id === editingStudent._id ? data.student : s))
        );
      } else {
        setStudents([...students, data.student]);
      }
      resetForm();
    } catch (error) {
      console.error("Error:", error);
      alert(`Error: ${error.message}`);
    }
  };

  const deleteStudent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this student?"))
      return;

    try {
      const response = await fetch(
        `http://localhost:5000/students/delete/${id}`,
        {
          method: "DELETE",
        }
      );
      const data = await response.json();

      if (!response.ok) throw new Error(data.message || "Delete failed");

      alert(data.message);
      setStudents(students.filter((s) => s._id !== id));
      setSelectedCourses((prev) => {
        const newState = { ...prev };
        delete newState[id];
        return newState;
      });
    } catch (error) {
      console.error("Error deleting student:", error);
      alert(`Error: ${error.message}`);
    }
  };

  const enrollStudent = async (studentId) => {
    const courseId = selectedCourses[studentId];
    if (!courseId) return alert("Please select a course first");

    try {
      const response = await fetch(
        `http://localhost:5000/students/enroll/${studentId}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ courseId }),
        }
      );
      const data = await response.json();

      if (!response.ok) throw new Error(data.message || "Enrollment failed");

      alert(`Success: ${data.message}`);
      fetchStudents();
      setSelectedCourses((prev) => {
        const newState = { ...prev };
        delete newState[studentId];
        return newState;
      });
    } catch (error) {
      console.error("Enrollment error:", error);
      alert(`Error: ${error.message}`);
    }
  };

  const resetForm = () => {
    setName("");
    setEmail("");
    setEditingStudent(null);
  };

  const filteredStudents = students.filter(
    (student) =>
      student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>üéì Student Management</h1>
        <p style={styles.subtitle}>
          Manage student records and course enrollments
        </p>
      </div>

      <div style={styles.controls}>
        <div style={styles.searchContainer}>
          <input
            type="text"
            placeholder="Search students..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={styles.searchInput}
          />
          <span style={styles.searchIcon}>üîç</span>
        </div>
      </div>

      <div style={styles.formCard}>
        <h3 style={styles.formTitle}>
          {editingStudent ? "Edit Student" : "Add New Student"}
        </h3>
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Full Name</label>
            <input
              type="text"
              placeholder="John Doe"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              style={styles.input}
            />
          </div>
          <div style={styles.formGroup}>
            <label style={styles.label}>Email Address</label>
            <input
              type="email"
              placeholder="john@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={styles.input}
            />
          </div>
          <div style={styles.buttonGroup}>
            <button
              type="submit"
              style={editingStudent ? styles.updateBtn : styles.addBtn}
            >
              {editingStudent ? "Update Student" : "Add Student"}
            </button>
            {editingStudent && (
              <button
                type="button"
                onClick={resetForm}
                style={styles.cancelBtn}
              >
                Cancel
              </button>
            )}
          </div>
        </form>
      </div>

      {isLoading ? (
        <div style={styles.loading}>
          <div style={styles.spinner}></div>
          <p>Loading students...</p>
        </div>
      ) : filteredStudents.length === 0 ? (
        <div style={styles.emptyState}>
          <p style={styles.emptyText}>No students found</p>
          {searchTerm && (
            <button
              onClick={() => setSearchTerm("")}
              style={styles.clearSearchBtn}
            >
              Clear search
            </button>
          )}
        </div>
      ) : (
        <div style={styles.grid}>
          {filteredStudents.map((student) => (
            <div key={student._id} style={styles.card}>
              <div style={styles.cardHeader}>
                <h3 style={styles.studentName}>{student.name}</h3>
                <p style={styles.studentEmail}>{student.email}</p>
              </div>

              <div style={styles.cardBody}>
                <div style={styles.section}>
                  <h4 style={styles.sectionTitle}>Enrolled Courses</h4>
                  {student.courses?.length > 0 ? (
                    <ul style={styles.courseList}>
                      {student.courses.map((course) => (
                        <li key={course._id} style={styles.courseItem}>
                          <span style={styles.courseName}>{course.name}</span>
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p style={styles.noCourses}>No enrolled courses</p>
                  )}
                </div>

                <div style={styles.section}>
                  <h4 style={styles.sectionTitle}>Enroll in Course</h4>
                  <div style={styles.enrollControls}>
                    <select
                      value={selectedCourses[student._id] || ""}
                      onChange={(e) =>
                        setSelectedCourses({
                          ...selectedCourses,
                          [student._id]: e.target.value,
                        })
                      }
                      style={styles.select}
                    >
                      <option value="">Select a course</option>
                      {courses
                        .filter(
                          (course) =>
                            !student.courses?.some((c) => c._id === course._id)
                        )
                        .map((course) => (
                          <option key={course._id} value={course._id}>
                            {course.name}
                          </option>
                        ))}
                    </select>
                    <button
                      onClick={() => enrollStudent(student._id)}
                      disabled={!selectedCourses[student._id]}
                      style={styles.enrollBtn}
                    >
                      Enroll
                    </button>
                  </div>
                </div>
              </div>

              <div style={styles.cardFooter}>
                <button
                  onClick={() => {
                    setEditingStudent(student);
                    setName(student.name);
                    setEmail(student.email);
                  }}
                  style={styles.editBtn}
                >
                  Edit
                </button>
                <button
                  onClick={() => deleteStudent(student._id)}
                  style={styles.deleteBtn}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

