import React from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

const Header = () => {
  return (
    <header style={styles.header}>
      <motion.div
        style={styles.navbar}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 120 }}
      >
        <motion.div style={styles.logoContainer} whileHover={{ scale: 1.03 }}>
          <Link to="/" style={styles.logoLink}>
            <span style={styles.logoIcon}>ðŸŽ“</span>
            <h1 style={styles.logoText}>Student Course Management</h1>
          </Link>
        </motion.div>

        <nav style={styles.navLinks}>
          <NavLink to="/">Home</NavLink>
          <NavLink to="/students">Students</NavLink>
          <NavLink to="/courses">Courses</NavLink>
        </nav>
      </motion.div>
    </header>
  );
};

const NavLink = ({ to, children }) => (
  <motion.div whileHover={{ y: -2 }}>
    <Link to={to} style={styles.link}>
      {children}
      <motion.span
        style={styles.linkUnderline}
        initial={{ scaleX: 0 }}
        whileHover={{ scaleX: 1 }}
        transition={{ duration: 0.3 }}
      />
    </Link>
  </motion.div>
);

// Professional, clean styling
const styles = {
  header: {
    position: "sticky",
    top: 0,
    zIndex: 100,
    width: "100%",
    backgroundColor: "#2c3e50",
    background: "linear-gradient(135deg, #2c3e50 0%, #3498db 100%)",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)",
    borderBottom: "1px solid rgba(255,255,255,0.1)",
  },
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "1.2rem 5%",
    maxWidth: "1200px",
    margin: "0 auto",
  },
  logoContainer: {
    display: "flex",
    alignItems: "center",
  },
  logoLink: {
    display: "flex",
    alignItems: "center",
    textDecoration: "none",
  },
  logoIcon: {
    fontSize: "2rem",
    marginRight: "0.8rem",
    filter: "drop-shadow(0 2px 2px rgba(0,0,0,0.2))",
  },
  logoText: {
    margin: 0,
    fontSize: "1.5rem",
    fontWeight: 600,
    color: "white",
    letterSpacing: "-0.5px",
  },
  navLinks: {
    display: "flex",
    gap: "2rem",
  },
  link: {
    color: "rgba(255,255,255,0.9)",
    textDecoration: "none",
    fontSize: "1.1rem",
    fontWeight: 500,
    padding: "0.5rem 0",
    position: "relative",
    transition: "color 0.2s ease",
    ":hover": {
      color: "white",
    },
  },
  linkUnderline: {
    position: "absolute",
    bottom: 0,
    left: 0,
    width: "100%",
    height: "2px",
    backgroundColor: "white",
    transformOrigin: "left center",
    borderRadius: "2px",
  },
};

export default Header;
