import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [activeTab, setActiveTab] = useState("all");

  // Fetch data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [studentsRes, coursesRes] = await Promise.all([
          fetch("http://localhost:5000/students"),
          fetch("http://localhost:5000/courses"),
        ]);
        const [studentsData, coursesData] = await Promise.all([
          studentsRes.json(),
          coursesRes.json(),
        ]);
        setStudents(studentsData);
        setCourses(coursesData);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  // Form submission handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = editingStudent
      ? `http://localhost:5000/students/update/${editingStudent._id}`
      : "http://localhost:5000/students/add";
    const method = editingStudent ? "PUT" : "POST";

    try {
      const response = await fetch(endpoint, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "Operation failed");

      alert(`Success: ${data.message}`);
      if (editingStudent) {
        setStudents(
          students.map((s) => (s._id === editingStudent._id ? data.student : s))
        );
      } else {
        setStudents([...students, data.student]);
      }
      resetForm();
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  // Delete student
  const deleteStudent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this student?"))
      return;

    try {
      const response = await fetch(
        `http://localhost:5000/students/delete/${id}`,
        {
          method: "DELETE",
        }
      );
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "Delete failed");

      alert(data.message);
      setStudents(students.filter((s) => s._id !== id));
      setSelectedCourses((prev) => {
        const newState = { ...prev };
        delete newState[id];
        return newState;
      });
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  // Enroll student in course
  const enrollStudent = async (studentId) => {
    const courseId = selectedCourses[studentId];
    if (!courseId) return alert("Please select a course first");

    try {
      const response = await fetch(
        `http://localhost:5000/students/enroll/${studentId}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ courseId }),
        }
      );
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "Enrollment failed");

      alert(`Success: ${data.message}`);
      setStudents(
        students.map((s) =>
          s._id === studentId
            ? { ...s, courses: [...s.courses, data.course] }
            : s
        )
      );
      setSelectedCourses((prev) => {
        const newState = { ...prev };
        delete newState[studentId];
        return newState;
      });
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  // Reset form
  const resetForm = () => {
    setName("");
    setEmail("");
    setEditingStudent(null);
  };

  // Filter students based on search and active tab
  const filteredStudents = students.filter((student) => {
    const matchesSearch =
      student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.email.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesTab =
      activeTab === "all" ||
      (activeTab === "enrolled" && student.courses?.length > 0) ||
      (activeTab === "unenrolled" &&
        (!student.courses || student.courses.length === 0));
    return matchesSearch && matchesTab;
  });

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <h1 style={styles.title}>🎓 Student Management</h1>
          <p style={styles.subtitle}>
            Manage student records and course enrollments
          </p>
        </div>
        <div style={styles.headerPattern}></div>
      </div>

      {/* Controls Section */}
      <div style={styles.controls}>
        <div style={styles.searchContainer}>
          <input
            type="text"
            placeholder="Search students..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={styles.searchInput}
          />
          <div style={styles.searchIcon}>
            <svg
              width="20"
              height="20"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
            >
              <circle cx="11" cy="11" r="8"></circle>
              <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
            </svg>
          </div>
        </div>

        <div style={styles.tabs}>
          <button
            style={activeTab === "all" ? styles.activeTab : styles.tab}
            onClick={() => setActiveTab("all")}
          >
            All Students
          </button>
          <button
            style={activeTab === "enrolled" ? styles.activeTab : styles.tab}
            onClick={() => setActiveTab("enrolled")}
          >
            Enrolled
          </button>
          <button
            style={activeTab === "unenrolled" ? styles.activeTab : styles.tab}
            onClick={() => setActiveTab("unenrolled")}
          >
            Unenrolled
          </button>
        </div>
      </div>

      {/* Form Card */}
      <div style={styles.formCard}>
        <h3 style={styles.formTitle}>
          {editingStudent ? "✏️ Edit Student" : "➕ Add New Student"}
        </h3>
        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.formGroup}>
            <label style={styles.label}>Full Name</label>
            <input
              type="text"
              placeholder="John Doe"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              style={styles.input}
            />
          </div>
          <div style={styles.formGroup}>
            <label style={styles.label}>Email Address</label>
            <input
              type="email"
              placeholder="john@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={styles.input}
            />
          </div>
          <div style={styles.buttonGroup}>
            <button
              type="submit"
              style={editingStudent ? styles.updateBtn : styles.addBtn}
            >
              {editingStudent ? "Update Student" : "Add Student"}
            </button>
            {editingStudent && (
              <button
                type="button"
                onClick={resetForm}
                style={styles.cancelBtn}
              >
                Cancel
              </button>
            )}
          </div>
        </form>
      </div>

      {/* Loading State */}
      {isLoading ? (
        <div style={styles.loadingContainer}>
          <div style={styles.spinner}></div>
          <p style={styles.loadingText}>Loading student data...</p>
        </div>
      ) : /* Empty State */
      filteredStudents.length === 0 ? (
        <div style={styles.emptyState}>
          <div style={styles.emptyIllustration}>
            <svg
              width="120"
              height="120"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
            >
              <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
              <circle cx="9" cy="7" r="4"></circle>
              <line x1="23" y1="11" x2="17" y2="11"></line>
            </svg>
          </div>
          <h3 style={styles.emptyTitle}>
            {searchTerm
              ? "No matching students found"
              : "No students available"}
          </h3>
          <p style={styles.emptyText}>
            {searchTerm
              ? "Try adjusting your search query"
              : activeTab === "enrolled"
              ? "No students are currently enrolled in courses"
              : activeTab === "unenrolled"
              ? "All students are enrolled in courses"
              : "Add your first student to get started"}
          </p>
          {searchTerm && (
            <button
              onClick={() => setSearchTerm("")}
              style={styles.clearSearchBtn}
            >
              Clear search
            </button>
          )}
        </div>
      ) : (
        /* Student Cards Grid */
        <div style={styles.grid}>
          {filteredStudents.map((student) => (
            <div key={student._id} style={styles.card}>
              <div style={styles.cardHeader}>
                <div style={styles.avatar}>
                  {student.name.charAt(0).toUpperCase()}
                </div>
                <div style={styles.studentInfo}>
                  <h3 style={styles.studentName}>{student.name}</h3>
                  <p style={styles.studentEmail}>{student.email}</p>
                </div>
              </div>

              <div style={styles.cardBody}>
                <div style={styles.section}>
                  <h4 style={styles.sectionTitle}>
                    <span style={styles.sectionIcon}>📚</span> Enrolled Courses
                  </h4>
                  {student.courses?.length > 0 ? (
                    <ul style={styles.courseList}>
                      {student.courses.map((course) => (
                        <li key={course._id} style={styles.courseItem}>
                          <span style={styles.courseName}>{course.name}</span>
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p style={styles.noCourses}>No courses enrolled</p>
                  )}
                </div>

                <div style={styles.section}>
                  <h4 style={styles.sectionTitle}>
                    <span style={styles.sectionIcon}>🎯</span> Enroll in Course
                  </h4>
                  <div style={styles.enrollControls}>
                    <select
                      value={selectedCourses[student._id] || ""}
                      onChange={(e) =>
                        setSelectedCourses({
                          ...selectedCourses,
                          [student._id]: e.target.value,
                        })
                      }
                      style={styles.select}
                    >
                      <option value="">Select a course</option>
                      {courses
                        .filter(
                          (course) =>
                            !student.courses?.some((c) => c._id === course._id)
                        )
                        .map((course) => (
                          <option key={course._id} value={course._id}>
                            {course.name}
                          </option>
                        ))}
                    </select>
                    <button
                      onClick={() => enrollStudent(student._id)}
                      disabled={!selectedCourses[student._id]}
                      style={styles.enrollBtn}
                    >
                      Enroll
                    </button>
                  </div>
                </div>
              </div>

              <div style={styles.cardFooter}>
                <button
                  onClick={() => {
                    setEditingStudent(student);
                    setName(student.name);
                    setEmail(student.email);
                  }}
                  style={styles.editBtn}
                >
                  Edit
                </button>
                <button
                  onClick={() => deleteStudent(student._id)}
                  style={styles.deleteBtn}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

