import React, { useEffect, useState } from "react";

const Students = () => {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [editingStudent, setEditingStudent] = useState(null);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    fetch("http://localhost:5000/students")
      .then((response) => response.json())
      .then((data) => setStudents(data))
      .catch((error) => console.error("Error fetching students:", error));
  };

  const addStudent = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/students/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setStudents([...students, data.student]);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error adding student:", error));
  };

  const updateStudent = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/students/update/${editingStudent._id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setStudents((prev) =>
          prev.map((s) => (s._id === editingStudent._id ? data.student : s))
        );
        setEditingStudent(null);
        setName("");
        setEmail("");
      })
      .catch((error) => console.error("Error updating student:", error));
  };

  const deleteStudent = (id) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      fetch(`http://localhost:5000/students/delete/${id}`, { method: "DELETE" })
        .then((response) => response.json())
        .then((data) => {
          alert(data.message);
          setStudents(students.filter((s) => s._id !== id));
        })
        .catch((error) => console.error("Error deleting student:", error));
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>🎓 Student Management</h2>

      {/* Student Form */}
      <form
        onSubmit={editingStudent ? updateStudent : addStudent}
        style={styles.form}
      >
        <input
          type="text"
          placeholder="Enter student name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={styles.input}
        />
        <input
          type="email"
          placeholder="Enter student email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.addButton}>
          {editingStudent ? "✏️ Update Student" : "➕ Add Student"}
        </button>
        {editingStudent && (
          <button
            onClick={() => setEditingStudent(null)}
            style={styles.cancelButton}
          >
            ❌ Cancel
          </button>
        )}
      </form>

      {/* Student List */}
      {students.length === 0 ? (
        <p style={styles.noStudents}>No students found.</p>
      ) : (
        <div style={styles.cardGrid}>
          {students.map((student) => (
            <div key={student._id} style={styles.card}>
              <h3 style={styles.studentName}>{student.name}</h3>
              <p style={styles.studentEmail}>{student.email}</p>
              <div style={styles.buttonGroup}>
                <button
                  onClick={() => {
                    setEditingStudent(student);
                    setName(student.name);
                    setEmail(student.email);
                  }}
                  style={styles.editButton}
                >
                  ✏️ Edit
                </button>
                <button
                  onClick={() => deleteStudent(student._id)}
                  style={styles.deleteButton}
                >
                  🗑️ Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// ✅ Updated Pro-Level CSS Styling
const styles = {
  container: {
    maxWidth: "800px",
    margin: "auto",
    padding: "20px",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
  },
  heading: {
    fontSize: "28px",
    fontWeight: "bold",
    marginBottom: "20px",
    color: "#333",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginBottom: "20px",
    backgroundColor: "#f9f9f9",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  input: {
    padding: "12px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  addButton: {
    backgroundColor: "#28a745",
    color: "white",
    padding: "12px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    transition: "0.3s",
  },
  addButtonHover: {
    backgroundColor: "#218838",
  },
  cancelButton: {
    backgroundColor: "#dc3545",
    color: "white",
    padding: "12px",
    fontSize: "16px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    marginTop: "10px",
    transition: "0.3s",
  },
  noStudents: {
    fontSize: "18px",
    fontWeight: "bold",
    color: "#666",
  },
  cardGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
    gap: "15px",
  },
  card: {
    backgroundColor: "#fff",
    padding: "15px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    textAlign: "left",
    transition: "0.3s",
  },
  cardHover: {
    transform: "scale(1.05)",
  },
  studentName: {
    fontSize: "18px",
    fontWeight: "bold",
    color: "#333",
  },
  studentEmail: {
    fontSize: "14px",
    color: "#555",
  },
  buttonGroup: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "10px",
  },
  editButton: {
    backgroundColor: "#ffc107",
    border: "none",
    padding: "8px 12px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  deleteButton: {
    backgroundColor: "#dc3545",
    border: "none",
    padding: "8px 12px",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Students;
